sensors.forEach(sensor => {
    let data = changes[sensor.key]?.changes || [];
    if (data.length === 0) {
        reportText += `${sensor.name}: Không có dữ liệu\n\n`;
        return;
    }

    // Tìm max, min, avg
    let maxVal = -Infinity, minVal = Infinity, sum = 0;
    let maxTime = null, minTime = null;

    data.forEach(d => {
        if (d.value > maxVal) {
            maxVal = d.value;
            maxTime = d.time;
        }
        if (d.value < minVal) {
            minVal = d.value;
            minTime = d.time;
        }
        sum += d.value;
    });

    let avg = (sum / data.length).toFixed(2);

    // Số lần cảnh báo
    let alertCount = changes.alerts?.[sensor.key] || 0;

    // Ghi vào báo cáo
    reportText += `${sensor.name}:\n`;
    reportText += `  - Max: ${maxVal}${sensor.unit} (lúc ${maxTime})\n`;
    reportText += `  - Min: ${minVal}${sensor.unit} (lúc ${minTime})\n`;
    reportText += `  - Avg: ${avg}${sensor.unit}\n`;
    reportText += `  - Số lần cảnh báo: ${alertCount}\n\n`;
});

