// Lấy timestamp từ dữ liệu Firebase
let dataTime = new Date(msg.payload.timestamp);
const firebaseDate = dataTime.toISOString().slice(0, 10);

// Lấy dữ liệu lưu trong flow
let storedDate = flow.get('date') || null;
let count = flow.get('count') || 0;
let avgTemperature = flow.get('avgTemperature') || 0;
let avgHumidity = flow.get('avgHumidity') || 0;
let avgGas = flow.get('avgGas') || 0;
let avgPM = flow.get('avgPM') || 0;

// Lấy thời gian đo trong ngày từ flow (nếu có)
let startTimeStr = flow.get('startTime') || null;
let endTimeStr = flow.get('endTime') || null;
let startTime = startTimeStr ? new Date(startTimeStr) : null;
let endTime = endTimeStr ? new Date(endTimeStr) : null;

// Nếu ngày mới trong Firebase → reset và set thời gian bắt đầu đo
if (storedDate !== firebaseDate) {
    count = 0;
    avgTemperature = 0;
    avgHumidity = 0;
    avgGas = 0;
    avgPM = 0;
    storedDate = firebaseDate;

    startTime = dataTime; // thời gian đo đầu tiên của ngày
    endTime = dataTime;   // lúc đầu cũng là thời gian kết thúc
} else {
    // Nếu chưa có startTime (trường hợp dữ liệu lỗi), gán luôn
    if (!startTime) startTime = dataTime;
    // Cập nhật thời gian kết thúc khi có dữ liệu mới trong cùng ngày
    endTime = dataTime;
}

// Lấy dữ liệu mới từ Firebase
const { temperature, humidity, gas, pm } = msg.payload;

if (
    typeof temperature === 'number' &&
    typeof humidity === 'number' &&
    typeof gas === 'number' &&
    typeof pm === 'number'
) {
    count += 1;

    avgTemperature = (avgTemperature * (count - 1) + temperature) / count;
    avgHumidity = (avgHumidity * (count - 1) + humidity) / count;
    avgGas = (avgGas * (count - 1) + gas) / count;
    avgPM = (avgPM * (count - 1) + pm) / count;

