    flow.set('date', storedDate);
    flow.set('count', count);
    flow.set('avgTemperature', avgTemperature);
    flow.set('avgHumidity', avgHumidity);
    flow.set('avgGas', avgGas);
    flow.set('avgPM', avgPM);
    flow.set('startTime', startTime.toISOString());
    flow.set('endTime', endTime.toISOString());

    // Format khoảng thời gian đo
    const timeRange = `${startTime.toLocaleString('vi-VN')} → ${endTime.toLocaleString('vi-VN')}`;

