let data = flow.get("allData") || [];

let start = flow.get("start_date");
let end = flow.get("end_date");

if (start == null || end == null) {
    node.warn("Chưa chọn ngày bắt đầu hoặc kết thúc");
    return null;
}

// Chuyển end thành cuối ngày để lọc đủ
let endDate = new Date(end);
endDate.setHours(23, 59, 59, 999);
let endTimestamp = endDate.getTime();

// Chuyển start sang timestamp
let startTimestamp = Number(start);

// Lọc theo trường timestamp
let filtered = data.filter(row => {
    let t = new Date(row.timestamp).getTime();
    return t >= startTimestamp && t <= endTimestamp;
});

// Nếu không có dữ liệu thì trả về mảng rỗng cho chart
if (filtered.length === 0) {
    msg.payload = [
        {
            series: ["Temperature", "Humidity"],
            data: [[], []],
            labels: []
        }
    ];
    return msg;
}

// Nhóm theo ngày và tính trung bình từng ngày
// Tạo một object để lưu trung bình theo ngày
let grouped = {};

filtered.forEach(row => {
    let day = row.timestamp.substring(0, 10); // lấy YYYY-MM-DD
    if (!grouped[day]) {
        grouped[day] = { tempSum: 0, tempCount: 0, humiSum: 0, humiCount: 0 };
    }
    let temp = Number(row.temperature);
    if (!isNaN(temp)) {
        grouped[day].tempSum += temp;
        grouped[day].tempCount++;
    }
    let humi = Number(row.humidity);
    if (!isNaN(humi)) {
        grouped[day].humiSum += humi;
        grouped[day].humiCount++;
    }
});

// Chuẩn bị dữ liệu cho chart
let labels = Object.keys(grouped).sort(); // ngày tăng dần
let avgTemps = [];
let avgHumis = [];

labels.forEach(day => {
    let g = grouped[day];
    avgTemps.push(g.tempCount ? parseFloat((g.tempSum / g.tempCount).toFixed(2)) : 0);
    avgHumis.push(g.humiCount ? parseFloat((g.humiSum / g.humiCount).toFixed(2)) : 0);
});

msg.payload = [
    {
        series: ["Average Temperature", "Average Humidity"],
        data: [avgTemps, avgHumis],
        labels: labels
    }
];
msg.ui_control = {
    options: {
        elements: {
            line: {
                fill: false
            },
            point: {
                radius: 4,
                hoverRadius: 6
            }
        }
    }
};

return msg;
