let sensors = [
    { key: "temperature", thresholdKey: "pending_Temperature", name: "Nhiệt độ", unit: "°C" },
    { key: "humidity", thresholdKey: "pending_Humidity", name: "Độ ẩm", unit: "%" },
    { key: "gas", thresholdKey: "pending_Gas", name: "Khí gas", unit: "ppm" },
    { key: "pm", thresholdKey: "pending_PM", name: "Bụi mịn", unit: "µg/m³" }
];

// Mảng chứa các cảnh báo cần gửi
let alerts = [];

sensors.forEach(sensor => {
    let value = flow.get(sensor.key);
    let threshold = flow.get(sensor.thresholdKey);
    let lastAlert = flow.get(`lastAlert_${sensor.key}`) || null;

    if (value !== undefined && threshold !== undefined) {
        if (value > threshold) {
            if (lastAlert === null || lastAlert !== value) {
                flow.set(`lastAlert_${sensor.key}`, value);
                alerts.push(`${sensor.name} hiện tại là ${value}${sensor.unit} đã vượt ngưỡng ${threshold}${sensor.unit}`);
            }
        } else {
            flow.set(`lastAlert_${sensor.key}`, null); // Reset khi an toàn
        }
    }
});

// Nếu có cảnh báo thì gửi email
if (alerts.length > 0) {
    msg.to = email;
    msg.topic = "CẢNH BÁO HỆ THỐNG (VUI LÒNG BẬT MÁY LỌC KHÔNG KHÍ)";
    msg.payload = alerts.join("\n"); // Ghép các cảnh báo thành nhiều dòng
    return msg;
}

