let sensors = [
    { key: "temperature", name: "Nhiệt độ", unit: "°C" },
    { key: "humidity",    name: "Độ ẩm", unit: "%" },
    { key: "gas",         name: "Khí gas", unit: "ppm" },
    { key: "pm",          name: "Bụi mịn", unit: "µg/m³" }
];

// Lấy dữ liệu thống kê hiện tại (trong ngày)
let changes = flow.get("sensorChanges") || {
    temperature: { lastValue: null, lastTime: null, changes: [] },
    humidity:    { lastValue: null, lastTime: null, changes: [] },
    gas:         { lastValue: null, lastTime: null, changes: [] },
    pm:          { lastValue: null, lastTime: null, changes: [] },
    alerts: { temperature: 0, humidity: 0, gas: 0, pm: 0 }
};

// Xác định loại dữ liệu hiện tại
let type = msg.topic; // Ví dụ: "temperature", "humidity"
let value = Number(msg.payload);
let now = new Date().toISOString();

if (!changes[type]) {
    node.warn(`Không xác định loại dữ liệu: ${type}`);
    return null;
}

let sensor = changes[type];

// Nếu giá trị thay đổi so với lần trước
if (sensor.lastValue === null || sensor.lastValue !== value) {
    sensor.lastValue = value;
    sensor.lastTime = now;
    sensor.changes.push({ value: value, time: now });

    let threshold = flow.get(`pending_${type.charAt(0).toUpperCase() + type.slice(1)}`);
    if (threshold !== undefined && value > threshold) {
        changes.alerts[type] += 1;
    }
}

// Lưu lại
changes[type] = sensor;
flow.set("sensorChanges", changes);

return null; // Không gửi ra đâu cả

// Lấy dữ liệu đã lưu trong ngày
let changes = flow.get("sensorChanges");
if (!changes) {
    node.warn("Chưa có dữ liệu để báo cáo");
    return null;
}

// Danh sách cảm biến
let sensors = [
    { key: "temperature", name: "Nhiệt độ", unit: "°C" },
    { key: "humidity",    name: "Độ ẩm", unit: "%" },
    { key: "gas",         name: "Khí gas", unit: "ppm" },
    { key: "pm",          name: "Bụi mịn", unit: "µg/m³" }
];

let reportText = "BÁO CÁO TỔNG HỢP NGÀY\n\n";

// Tính toán cho từng cảm biến
sensors.forEach(sensor => {
    let data = changes[sensor.key]?.changes || [];
    if (data.length === 0) {
        reportText += `${sensor.name}: Không có dữ liệu\n\n`;
        return;
    }

    // Tìm max, min, avg
    let maxVal = -Infinity, minVal = Infinity, sum = 0;
    let maxTime = null, minTime = null;

    data.forEach(d => {
        if (d.value > maxVal) {
            maxVal = d.value;
            maxTime = d.time;
        }
        if (d.value < minVal) {
            minVal = d.value;
            minTime = d.time;
        }
        sum += d.value;
    });

    let avg = (sum / data.length).toFixed(2);

    // Số lần cảnh báo
    let alertCount = changes.alerts?.[sensor.key] || 0;

    // Ghi vào báo cáo
    reportText += `${sensor.name}:\n`;
    reportText += `  - Max: ${maxVal}${sensor.unit} (lúc ${maxTime})\n`;
    reportText += `  - Min: ${minVal}${sensor.unit} (lúc ${minTime})\n`;
    reportText += `  - Avg: ${avg}${sensor.unit}\n`;
    reportText += `  - Số lần cảnh báo: ${alertCount}\n\n`;
});

// Gửi email
let email = flow.get("email");//"felina56723t@gmail.com";
msg.to = email;
msg.topic = "Báo cáo tổng hợp trong ngày";
msg.payload = reportText;

// Reset dữ liệu để bắt đầu ngày mới
